import React, { useState } from 'react';
import './App.css';

function App() {
  const [players, setPlayers] = useState([]);
  const [playerName, setPlayerName] = useState('');
  const [playerTier, setPlayerTier] = useState('Bronze');
  const [teams, setTeams] = useState({ team1: [], team2: [] });
  const [showTeams, setShowTeams] = useState(false);
  const [sortByTier, setSortByTier] = useState(false);

  // Định nghĩa các tier và điểm số
  const tiers = {
    'Bronze': { value: 1, color: '#CD7F32', icon: '🥉' },
    'Silver': { value: 2, color: '#C0C0C0', icon: '🥈' },
    'Gold': { value: 3, color: '#FFD700', icon: '🥇' },
    'Diamond': { value: 4, color: '#B9F2FF', icon: '💎' },
    'Master': { value: 5, color: '#8A2BE2', icon: '👑' }
  };

  // Thêm người chơi
  const addPlayer = () => {
    if (playerName.trim() && !players.includes(playerName.trim())) {
      setPlayers([...players, playerName.trim()]);
      setPlayerName('');
    }
  };

  // Xóa người chơi
  const removePlayer = (index) => {
    const newPlayers = players.filter((_, i) => i !== index);
    setPlayers(newPlayers);
    setShowTeams(false);
  };

  // Chia đội ngẫu nhiên
  const divideTeams = () => {
    if (players.length < 2) {
      alert('Cần ít nhất 2 người chơi để chia đội!');
      return;
    }

    // Tạo bản sao mảng người chơi và xáo trộn
    const shuffledPlayers = [...players].sort(() => Math.random() - 0.5);

    const team1 = [];
    const team2 = [];

    // Chia đều người chơi
    shuffledPlayers.forEach((player, index) => {
      if (index % 2 === 0) {
        team1.push(player);
      } else {
        team2.push(player);
      }
    });

    setTeams({ team1, team2 });
    setShowTeams(true);
  };

  // Chia lại đội
  const reshuffleTeams = () => {
    divideTeams();
  };

  // Reset tất cả
  const resetAll = () => {
    setPlayers([]);
    setTeams({ team1: [], team2: [] });
    setShowTeams(false);
    setPlayerName('');
  };

  // Xử lý phím Enter
  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      addPlayer();
    }
  };

  return (
    <div className="app">
      <div className="container">
        <h1 className="title">⚽ Chia Đội Bóng</h1>

        {/* Form thêm người chơi */}
        <div className="input-section">
          <div className="input-group">
            <input
              type="text"
              value={playerName}
              onChange={(e) => setPlayerName(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Nhập tên người chơi..."
              className="player-input"
            />
            <button onClick={addPlayer} className="add-btn">
              Thêm
            </button>
          </div>
        </div>

        {/* Danh sách người chơi */}
        {players.length > 0 && (
          <div className="players-section">
            <h3>Danh sách người chơi ({players.length})</h3>
            <div className="players-list">
              {players.map((player, index) => (
                <div key={index} className="player-item">
                  <span>{player}</span>
                  <button 
                    onClick={() => removePlayer(index)}
                    className="remove-btn"
                  >
                    ✕
                  </button>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Nút điều khiển */}
        {players.length >= 2 && (
          <div className="control-buttons">
            <button onClick={divideTeams} className="divide-btn">
              Chia Đội
            </button>
            <button onClick={resetAll} className="reset-btn">
              Reset Tất Cả
            </button>
          </div>
        )}

        {/* Hiển thị kết quả chia đội */}
        {showTeams && (
          <div className="teams-section">
            <div className="teams-container">
              <div className="team">
                <h3 className="team-title team1">Đội 1 ({teams.team1.length})</h3>
                <div className="team-members">
                  {teams.team1.map((player, index) => (
                    <div key={index} className="team-member team1-member">
                      {player}
                    </div>
                  ))}
                </div>
              </div>

              <div className="vs-divider">VS</div>

              <div className="team">
                <h3 className="team-title team2">Đội 2 ({teams.team2.length})</h3>
                <div className="team-members">
                  {teams.team2.map((player, index) => (
                    <div key={index} className="team-member team2-member">
                      {player}
                    </div>
                  ))}
                </div>
              </div>
            </div>

            <button onClick={reshuffleTeams} className="reshuffle-btn">
              Chia Lại Đội
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
